def count_journalists
    test = $journalists.count
    puts "On compte actuellement #{test} journalistes."
end

def shortest
        test = $journalists.sort_by(&:length)
        puts "#{test[0]} a le blaze le plus court."
end

def five
    test = $journalists.map{|i| i.gsub"@",""} 
    test.select! {|i| i.length == 5}
    #journalists = journalists.map { |word| "@#{word}" }
    puts "Il y a #{test.count} journalistes qui ont un nom qui comporte 5 caracteres."
end

def capital_letter
    a = $journalists.map{|handle| handle.gsub"@",""}
    b = a.map{|handle| handle.gsub"_",""}
    test = b.select {|handle| handle[0] == handle[0].capitalize}
    puts "Il y a #{test.count} journalistes dont le pseudo commence par une majuscule"
end

def alpha_sort
    test = $journalists.sort    
    puts test
end

def length_sort
    test = $journalists.sort_by(&:length)
    puts test
end

def find
    puts "Tu cherches qui ? Oublie pas le signe @"
    input = gets.chomp
    test = $journalists.index(input)
    puts "#{input} est a la ligne #{test}."
end

